import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { Laptop, Lmobile, SLaptop, stablet, tablet } from "../responsive";
import styled from "styled-components";
import axios from "axios";
import { Link } from "react-router-dom";
import { Close, FirstPage, KeyboardArrowLeft, KeyboardArrowRight, LastPage, Menu } from "@mui/icons-material";
import { useEffect, useState } from "react";
import { useContext } from "react";
import { UserContext } from "../context/context";
import { MenuItem, Select } from "@mui/material";

const Con = styled.div`
  position: relative;
  width: 100%;
  height: 100dvh;
  background: #dbeafe;
`;

const Container = styled.div`
  width: 100%;
    // height: 88dvh;
    // height: 10%;
    // background: red;
  color: black;
//   overflow-y: scroll;
`;
  const Wrapper = styled.div`
    //   background: white;
    border-radius: 7px;
    padding: 20px;
    padding: 30px 50px;
    gap: 1rem;
    width: 100%;
    // height: 65%;
    box-sizing: border-box;
    position:relative;
  `;
  
  const NavbarCon = styled.div`
    background: #dbeafe;

    position: sticky;
    top: 0;
    z-index: 999;
  `;
  const Navbar = styled.div`
//   height:12dvh;
  width: 100%;
  // background:red;
  padding: 30px 50px;
  box-sizing: border-box;
  // border-bottom:1px solid white;
  box-shadow: 0px 0px 5px rgba(0, 0, 0, 0.3);
`;
const NavbarLeft = styled.div`
  flex: 1;
  font-size: 25px;
  font-weight: 600;
  ${Lmobile({ fontSize: "20px" })};
`;
const NavbarMiddle = styled.div`
  gap: 1rem;
  flex: 3;
  display: flex;
  ${tablet({ display: "none" })};
`;
const NavbarRight = styled.div`
  flex: 1;
  gap: 1rem;
  ${SLaptop({ display: "none" })};
`;


const NavbarText = styled.p`
  white-space: nowrap;
  font-size: 0.9rem;
  cursor: pointer;
`;

const NavbarMenu = styled.div`
  display: none;
  font-size: 35px;
  cursor: pointer;
  ${Lmobile({ fontSize: "25px" })};
  ${tablet({ display: "flex" })};
`;

const NavbarButton = styled.button`
  background: ${(props) => (props.outline ? " transparent" : "#2563eb")};
  border: ${(props) => (props.outline ? " 1px solid #2563eb" : "none")};
  color: ${(props) => (props.outline ? " black" : "white")};
  padding: 10px 20px;
  flex: 1;
  border-radius: 30px;
  flex-basis: 10px;
  max-width: 240px;
  cursor: pointer;
  font-weight: 700;
`;

const SideMenuCon = styled.div`
  position: absolute;
  left: 0;
  z-index: 99999;
  width: 0;
  height: 100dvh;
  background: rgba(255, 255, 255, 0.9);
  background: whitesmoke;
  top: 0;
  overflow: hidden;
  text-slign: center;
  gap: 1rem;
`;

const SideMenuButton = styled.button`
  background: ${(props) => (props.outline ? " transparent" : "#2563eb")};
  border: ${(props) => (props.outline ? " 1px solid #2563eb" : "none")};
  color: ${(props) => (props.outline ? " black" : "white")};
  padding: 10px 20px;
  // flex: 1;
  border-radius: 30px;
  flex-basis: 10px;
  max-width: 240px;
  cursor: pointer;
  font-weight: 700;
  width: 100%;
`;

const TableCon = styled.div`
  width: 100%;
  background: white;
  overflow: hidden;
  height: 100%;
  height: 450px;
  ${stablet({ display: "none" })};
`;
const TableHeaderCon = styled.div`
  border: 1px solid #eee;
  width: 100%;
  display: grid;
  grid-template-columns: 1fr 2fr 2fr 2fr 2fr;
  background:#eee;
  height:35px;
`;
const TableHeader = styled.div`
   border-left:1px solid gray;
   padding:5px 10px;
   box-sizing:border-box;
   height:100%;
`
const TableHeaderText = styled.p`
//   color:#2563eb;
  font-weight:700;
  ${tablet({ fontSize: "14px" })};
`;
const Table = styled.div`
  height:94%;
  overflow-y:scroll;
//   overflow-x:auto;
//   height:100%;
`;
const TableRows = styled.div`
  display: grid;
  width: 100%;
  grid-template-columns: 1fr 2fr 2fr 2fr 2fr;
  border: 1px solid #eee;
  // height:100%;
`;

const TableColumns = styled.div`
  // background:red;
  border-right: 1px solid #eee;
  padding: 5px 10px;
  box-sizing: border-box;
  ${tablet({ fontSize: "14px" })};
  white-space: wrap;
  text-overflow: ellipsis;
  display: block;
  overflow: hidden;
`;

const TableColumnsButton = styled.button`
  ${tablet({ fontSize: "11px",width:"50px" })};
  padding:7px;
  border-radius:7px;
  font-size:15px;
  border:none;
  color:white;
  width:70px;
`;

const MobileTableCon = styled.div`
  display:none;
  ${stablet({display:"block"})};
  background:white;
  height:100%;
  width:100%;
  padding:5px;
  border-radius:7px;
`
const MobileTable = styled.div`
  margin:5px 0;
  background:#f3f4fc;
  padding:5px;
`
const MobileTableColumns = styled.div`
  border-bottom:1px solid #eee;
`
const MobileTableKey = styled.p`
   color:#007ec3;
`
const MobileTableValue = styled.p``

const PaginationsWrapper = styled.div`
  width: 100%;
  // position:sticky;
  // top:100px;
  padding: 10px 50px;
  box-sizing:border-box;
`;
const PaginationsCon = styled.div`
   justify-content:space-evenly;
   gap:5px;
`;
const ItemsPerPageCon = styled.div`
gap:6px;
`
const ItemsPerPageText = styled.p`
  ${tablet({ fontSize: "14px" })};
`;
const PaginationsEntries = styled.div``
const PaginationsEntriesText = styled.div`
  gap: 0.3rem;
  ${tablet({ fontSize: "14px" })};
`;
const PaginationsButtonsCon = styled.div`
gap:.5rem;
`
const PaginationsButton = styled.div`
  padding: 5px;
  border: none;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size:20px;
  ${tablet({ fontSize: "16px" })};

  &:hover {
    background: #eee;
    transition: 2s all;
    cursor: pointer;
  }
  //    color:white;
`;

const UsersList = () => {
  const user = useContext(UserContext);
  const [sideMenuOpen, setSideMenuOpen] = useState(false);
  const [itemsPerpage,setItemsPerPage]=useState(20);
  const [currentPage,setCurrentPage]=useState(1);
 useEffect(() => {
  console.log(itemsPerpage)
  setCurrentPage(1)
 }, [itemsPerpage])
 

 const SideMenu = () => {
    return (
      <SideMenuCon
        className="flex aic jcc fdc"
        style={{ width: sideMenuOpen ? "75%" : "0" }}
      >
        <NavbarText>
          <Link to="/reset-password" className="link">
            Reset Password
          </Link>
        </NavbarText>
        <NavbarText>
          <Link to="/users-list" className="link">
            Get all users
          </Link>
        </NavbarText>
        <NavbarText>Switch to owner</NavbarText>
        {/* <NavbarText>Delete account</NavbarText> */}
        <NavbarText>Logout</NavbarText>
        {user.accessToken ? (
          <SideMenuButton>LOGOUT</SideMenuButton>
        ) : (
          <>
            <SideMenuButton outline>
              <Link to="register" className="link">
                Register
              </Link>
            </SideMenuButton>
            <SideMenuButton>
              <Link to="login" className="link">
                Login
              </Link>
            </SideMenuButton>
          </>
        )}
      </SideMenuCon>
    );
  };

  const handleUpdateRole = async () => {
    try {
      const res = await axios.post("");
    } catch (err) {
      console.log(err);
    }
  };

  const demoUsersList = [
    {
        id:0,
        username:"user1",
        email:"user1email.com",
        role:"user",
        verifiedEmail:false
    },
