import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { tablet } from "../responsive";
import styled from "styled-components";
const Container = styled.div`
  width: 100vw;
  height: 100dvh;
  background: #dbeafe;
  color: #3c3c3c;
`;
const Wrapper = styled.div`
background:white;
border-radius:7px;
padding:20px;
//   width: 30%;
  // height: 65%;
  ${tablet({ width: "80%" })};
`;

const Header = styled.div``
const HeaderMainText = styled.h2`
font-weight:700;
`;
const HeaderSmText = styled.p``;

const OtpPinCon = styled.div`
gap:5rem;
`
const OtpPins = styled.div`  
  height:3rem;
  width:3rem;

`
const OtpPinsInput = styled.input`
outline:1px solid blue;
height:100%;
width:100%;
border:1px solid #eee;
`

const VerifyOTP = () => {
 
  return (
    <Container className="flex aic jcc">
     <Wrapper>
     <Header className="w100 flex aic jcc fdc" >
        <HeaderMainText>Please enter the One-Time Password to verify your account</HeaderMainText>
        <HeaderSmText>A One Time PAssword has been sent to email
        </HeaderSmText>
     </Header>
     <OtpPinCon className="flex aic jcc " >
        <OtpPins>
            <OtpPinsInput maxLength='1' type="number" />
        </OtpPins>
        <OtpPins>
            <OtpPinsInput max={1} type="number" />
        </OtpPins>
        <OtpPins>
            <OtpPinsInput type="number" />
        </OtpPins>
        <OtpPins>
            <OtpPinsInput type="number" />
        </OtpPins>
        <OtpPins>
            <OtpPinsInput type="number" />
        </OtpPins>
     </OtpPinCon>
     </Wrapper>
    </Container>
  );
};

export default VerifyOTP;
