import {
  FacebookRounded,
  Google,
  Lock,
  MailOutlined,
  RemoveRedEye,
} from "@mui/icons-material";
import styled from "styled-components";

const Container = styled.div`
  background: url(./hero-bg.png);
  background-repeat: no-repeat;
  background-position: center;
  background-size: cover;
  box-sizing: border-box;
  position: relative;
  padding-top: 85px;
  margin-bottom: 500px;
  height: 90vh;
`;
const SignUp_Con = styled.div`
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  box-sizing: border-box;
  padding: 0 5vh;
  height: 650px;
  margin-top:50px;
  margin-bottom:120px;
  `;
  
  const Wrapper = styled.div`
  box-shadow: -1.04px 4.891px 20px 0px rgb(69 49 183 / 50%);
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: flex-start;
  height: 100%;
  box-sizing: border-box;
`;
const Left_Con = styled.div`
  padding: 1vh 5vh;
  flex: 2;
  background-color: white;
  box-sizing: border-box;
  height: 100%;
`;

const Left_ConHeader = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  flex-direction: column;
  width: 100%;
  margin: 50px 0;
`;
const LeftConText = styled.p`
  color: black;
  font-size: ${(props) => props.fz};
  font-weight: ${(props) => props.fw};
  color: ${(props) => props.cl};
  margin: ${(props) => props.m};
  color: black;
`;

const SignUp_Method = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  width: 100%;
`;
const IconContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  color: ${(props) => props.cl};
  margin-right: 5px;
`;

const LogIn_Button = styled.button`
  display: flex;
  align-items: center;
  justify-content: center;
  // width: 100%;
  border: 1px solid #eee;
  border-radius: 15px;
  padding: 16px 40px;
  color: black;
  flex: 1;
  margin: 0 5px;
  cursor: pointer;
  background-color: white;
  font-size:17px;
  font-weight:300;

  &:hover ${IconContainer} {
    color: white;
  }

  &:hover {
    color: white;
    background-color: #ee4730;
  }
`;

const Other_Methods = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  margin: 40px 0;
`;
const Other_MethodsText = styled.p`
  margin: 0 10px;
`;
const Hr = styled.hr`
  width: 100%;
  border: none;
  height: 1px;
  background-color: #eee;
`;
const SignUp_Form = styled.form`
  width: 100%;
`;

const SignUp_InputCon = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  border: 1px solid #eee;
  flex: 1;
  padding: 0 30px;
  height: 60px;
  color: grey;
  margin: 20px 0;
`;
const SignUp_Input = styled.input`
  outline: none;
  border: none;
  height: 100%;
  width: 100%;
  flex: 1;
  margin-left: 10px;

  &::placeholder {
    font-size: 17px;
  }
`;
const TermsAndPolicy = styled.div`
  display: flex;
  align-items: center;
  justify-content: flex-start;
  margin-top: 25px;
`;
const TermsAndPolicy_Input = styled.input`
  border: 1px solid red;
  padding: 15px;
  margin-right: 10px;
`;
const TermsAndPolicy_Txt = styled.div`

`;
const SubmitCon = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
`;
const Submit = styled.button`
  background: linear-gradient(2deg, #3da9f5 0%, #683df5 100%);
  box-shadow: -1.04px 4.891px 20px 0px rgb(69 49 183 / 50%);
  border: none;
  color: white;
  padding: 15px 30px;
  border-radius: 30px;
  font-weight: 500;
  font-size: 18px;
  cursor: pointer;
  margin-top:20px;
`;

const Right_Con = styled.div`
  background:-webkit-linear-gradient(90deg, #5140fe 0%, #7757f7 56%, #9d6ef0 100%);
  flex: 1;
  display: flex;
  align-items: center;
  justify-content: center;
  height: 100%;
`;
const RightCon_TxtCon = styled.div`
  background: -webkit-linear-gradient(
    90deg,
    #5140fe 0%,
    #7757f7 56%,
    #9d6ef0 100%
  );
  height: 60%;
  padding: 0 20px;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
`;
const RightCon_Txt = styled.p`
  color: white;
  font-size: ${(props) => props.fz};
  font-weight: ${(props) => props.fw};
  text-align: center;
  display: flex;
  align-items: center;
  justify-content: center;
  margin:10px 0;
`;

const RightCon_Button = styled.button`
  box-shadow: -1.04px 4.891px 20px 0px rgb(69 49 183 / 50%);
  border: 1px solid white;
  color: white;
  padding: 15px 30px;
  border-radius: 30px;
  font-weight: 500;
  font-size: 18px;
  cursor: pointer;
  background:transparent;
`;

function SignUp() {
  const ToggleVisibility = () => {
    if (document.getElementById("SignUp_Password").type === "password") {
      document.getElementById("visiblePassword_Icon").style.color = "grey";
      document.getElementById("SignUp_Password").type = "text";
    } else {
      document.getElementById("SignUp_Password").type = "password";
      // document.getElementById("visiblePassword_Icon").style.color = "black";
    }
  };

  return (
    <Container>
      <SignUp_Con>
        <Wrapper>
          <Left_Con>
            <Left_ConHeader>
              <LeftConText m="10px 0 " fz="40px" fw={600}>
                SIGN UP
              </LeftConText>
              <LeftConText m="10px 0 " cl="grey">
                We're happy you're here!
              </LeftConText>
            </Left_ConHeader>
            <SignUp_Method>
              <LogIn_Button className="LogInHover" fw={400}>
                <IconContainer cl="#3b5998">
                  <FacebookRounded />
                </IconContainer>
                Log in with Facebook
              </LogIn_Button>
              <LogIn_Button className="LogInHover" fw={400}>
                <IconContainer cl="#db4a39">
                  <Google sx={{ fontSize: "15px" }} />
                </IconContainer>
                Log in with Google
              </LogIn_Button>
            </SignUp_Method>
            <Other_Methods>
              <Hr />
              <Other_MethodsText m="10px">Or</Other_MethodsText>
              <Hr />
            </Other_Methods>
            <SignUp_Form>
              <SignUp_InputCon>
                <MailOutlined />
                <SignUp_Input placeholder="Email Address" type="text" />
              </SignUp_InputCon>

              <SignUp_InputCon>
                <Lock />
                <SignUp_Input
                  id="SignUp_Password"
                  placeholder=" Password"
                  type="password"
                />
                <RemoveRedEye
                  sx={{ cursor: "pointer" }}
                  onClick={ToggleVisibility}
                  id="visiblePassword_Icon"
                />
              </SignUp_InputCon>
              <TermsAndPolicy>
                <TermsAndPolicy_Input type="checkbox" />
                <TermsAndPolicy_Txt>
                  The Sbidu Terms of Use apply
                </TermsAndPolicy_Txt>
              </TermsAndPolicy>
              <SubmitCon>
                <Submit>Log In</Submit>
              </SubmitCon>
            </SignUp_Form>
          </Left_Con>

          <Right_Con>
            <RightCon_TxtCon>
            <RightCon_Txt fz="40px" fw={600}>ALREADY HAVE AN ACCOUNT?</RightCon_Txt>
            <RightCon_Txt>Log in and go to your Dashboard.</RightCon_Txt>
