import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { tablet } from "../responsive";
import styled from "styled-components";
import OTPInput from "react-otp-input";
import { useState } from "react";
import axios from "axios";
import { useContext } from "react";
import { UserContext } from "../context/context";
import { useEffect } from "react";
const Container = styled.div`
  width: 100vw;
  height: 100dvh;
  background: #dbeafe;
  color: #3c3c3c;
`;
const Wrapper = styled.div`
  background: white;
  border-radius: 7px;
  padding: 20px;
  gap: 1rem;
  //   width: 30%;
  // height: 65%;
  ${tablet({ width: "80%" })};
`;

const ProfilePicCon = styled.div``;
const ProfilePic = styled.div``;
const HeaderMainText = styled.h2`
  font-weight: 700;
  text-align: center;
`;
const HeaderSmText = styled.p`
  font-size: 0.79rem;
  text-align: center;
`;
const HeaderBdText = styled.h4``;

const ResetEmail = styled.div`
  width: 100%;
  text-align: center;
`;
const ResetEmailBgText = styled.h4``;
const ResetEmailBdText = styled.span`
  margin: 0 2px;
  font-size: 0.91rem;
`;

const ChangeEmailCon = styled.div`
  width: 60%;
`;
const ChangeEmailInput = styled.input`
  width: 100%;
  height: 100%;
  outline: none;
  border: none;
  border-bottom: 1px solid grey;
  // border-radius: 5px;
  padding: 5px 3px;
  transition: 0.5s all;

  &:focus {
    border-bottom: 1.8px solid #2563eb;
  }
`;
const ChangeEmailButton = styled.div``;

const OtpPinsInput = styled.input`
  outline: none;
  height: 100%;
  width: 100%;
  border: none;
  border-bottom: 2px solid grey;
  margin: 0 10px;
  font-size: 30px;
`;

const ButtonCon = styled.div``;
const Button = styled.button`
  background: #2563eb;
  padding: 0.6rem 2.2rem;
  border-radius: 4px;
  color: white;
  border: none;
  outline: none;
  font-size: 15px;
  font-weight: 600;
`;

const Home = () => {
 

  return (
    <Container className="flex aic jcc">
     <Wrapper  className="flex aic jcc fdc bgw">
            
     </Wrapper>
    </Container>
  );
};

export default Home;
