import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { Laptop, tablet } from "../responsive";
import styled from "styled-components";
import OTPInput from "react-otp-input";
import { useState } from "react";
import axios from "axios";
import { useContext } from "react";
import { UserContext } from "../context/context";
import { useEffect } from "react";
import { ArrowBack, DeleteOutline, KeyboardDoubleArrowLeft, PersonOutline, RemoveRedEye, VisibilityOff } from "@mui/icons-material";
import { useRef } from "react";
const Container = styled.div`
  width: 100vw;
  height: 100dvh;
  background: #dbeafe;
  color: black;
  position:relative;
`;
const Wrapper = styled.div`
  background: white;
  border-radius: 7px;
  padding: 20px;
  gap: 1rem;
  width: 30%;
  // height: 65%;
  ${Laptop({ width: "50%" })};
  ${tablet({ width: "80%" })};
`;

const ProfilePicCon = styled.div``;
const ProfilePicWrapper = styled.div`
  max-width: 200px;
  max-height: 200px;
  border-radius: 50%;
  min-width: 0;
  min-height: 0;
  // border: 1px solid gray;
  ${tablet({ maxWidth: "100px",maxHeight:"100px" })};
`;
const ProfilePic = styled.img`
  height: 100%;
  border-radius: 50%;
  width: 100%;
  object-fit: contain;
`;
const HeaderCon = styled.div``
const HeaderMainText = styled.h2`
  font-weight: 700;
  color: black;
  text-align: center;
`;
const HeaderSmText = styled.p`
  font-size: 0.99rem;
  text-align: center;
`;
const HeaderRdmText = styled.p`
  font-size: 0.79rem;
  text-align: center;
  text-overflow: ellipsis;
  overflow: hidden;
  display: inline-block;
//   max-width: 10px;
${tablet({display:''})}
`;

const ResetPasswordCon = styled.div`
  width: 100%;
  text-align: center;
  width: 100%;
  position: absolute;
  top: 20px;
  // background:red;
  padding: 0 50px;
  box-sizing: border-box;
  // margin:25px 0;
`;
const ResetPassword = styled.div`
cursor:pointer;
`;
const ResetPasswordBdText = styled.span`
margin: 0 2px;
  font-size: 0.99rem;
`;
const DeleteAccountCon = styled.div`
  width: 100%;
  text-align: center;
  width: 100%;
  position: absolute;
  bottom:30px;
  padding:0 20px;
  box-sizing:border-box;
  `;
  const DeleteAccount = styled.div`
  background:red;
cursor:pointer;
color:white;
padding:10px;
border-radius:9px;
${tablet({padding:"6px"})};
`;
const DeleteAccountBdText = styled.span`
  margin: 0 2px;
  font-size: 0.99rem;
`;

const ChangeEmailCon = styled.div`
  width: 60%;
`;
const ChangeEmailInput = styled.input`
  width: 100%;
  height: 100%;
  outline: none;
  border: none;
  border-bottom: 1px solid grey;
  // border-radius: 5px;
  padding: 5px 3px;
  transition: 0.5s all;

  &:focus {
    border-bottom: 1.8px solid #2563eb;
  }
`;
const ChangeEmailButton = styled.div``;


const ButtonCon = styled.div`
   gap:10px;
`;
const Button = styled.button`
  background: #2563eb;
  padding: 0.6rem 2.2rem;
  border-radius: 4px;
  color: white;
  border: none;
  outline: none;
  font-size: 15px;
  font-weight: 600;
  cursor:pointer;

  &:hover{
    transform:scale(1.05);
    transition:.4s all;
  }
`;

const ButtonSpan = styled.span`
  font-size:20px;
`

const ResetPassTextCon = styled.div`
  margin: 10px 0;
  ${tablet({ margin: "5px 0" })};
`;
const ResetPassText = styled.p`
  // text-align:center;
  font-weight:700;
  font-size:2rem;
  ${tablet({fontSize:"1.2rem"})};
`
const ResetPassWrapper = styled.div`
width:100%;
`
const ResetPassCon = styled.div`
   display:flex;
   align-items:Center;
   justify-content:center;
   gap:1rem;
   flex-direction:column;
   width:100%;
`
const Back = styled.div`

`;
const BackBtn = styled.button`
color:white;
border-radius:4px;
padding:5px 0;
width:70px;
margin:13px 0;
cursor:pointer;
  background: #2563eb;
  border:none;
`;
;

const InputCon = styled.div`
  background: white;
  width:100%;
`;
const Input = styled.input`
  background: white;
  width: 100%;
  // height: 100%;
  padding: 7px;
  border: none;
  outline: none;
  border: 1px solid #eee;
  border-radius: 6px;
  box-sizing: border-box;

  &:focus {
    background: #eee;
  }

  &::placeholder {
    font-size: 12px;
  }
`;

const Inputlabel = styled.label`
  font-size: 15px;
  font-weight: 600;
  background: white !important;
  margin: 7px 0;
`;
const InputContainer = styled.div`
  border: 1px solid #eee;
  flex: 1;
  // padding: 0 30px;
  // height: 60px;
  color: grey;
  position: relative;
  // margin: 20px 0;
`;

const Home = () => {
  const PasswordInputRef = useRef([]);
  const [resetPass,setResetPass]=useState(true);
    const [passVisible, setPassVisible] = useState(false);


   const ToggleVisibility = (currentInput) => {
     const passwordDisplayType = PasswordInputRef.current[currentInput].type;
     if (passwordDisplayType === "password") {
       PasswordInputRef.current[currentInput].type = "text";
       setPassVisible(currentInput);
     } else {
       PasswordInputRef.current[currentInput].type = "password";
       setPassVisible(null);
     }
   };


   const handleResetPassword = ()=>{
    setResetPass(current=>!current)
   }
 

  return (
    <Container className="flex aic jcc fdc">
      <Wrapper className="flex aic jcc fdc bgw">
        <ProfilePicCon className="flex aic jcc w100">
          <ProfilePicWrapper className="flex aic jcc">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              // xmlns:xlink="http://www.w3.org/1999/xlink"

              width="100%"
              height="100%"
              viewBox="0 0 172 172"
              className="w100 h100"
            >
              <defs data-v-871b8aab="">
                <filter
                  id="Ellipse_3512"
                  x="0"
                  y="0"
                  width="172"
                  height="172"
                  filterUnits="userSpaceOnUse"
                  data-v-871b8aab=""
                >
                  <feOffset
                    dy="3"
                    input="SourceAlpha"
                    data-v-871b8aab=""
                  ></feOffset>
                  <feGaussianBlur
                    stdDeviation="3"
                    result="blur"
                    data-v-871b8aab=""
                  ></feGaussianBlur>
                  <feFlood flood-opacity="0.161" data-v-871b8aab=""></feFlood>
                  <feComposite
                    operator="in"
                    in2="blur"
                    data-v-871b8aab=""
                  ></feComposite>
                  <feComposite
                    in="SourceGraphic"
                    data-v-871b8aab=""
                  ></feComposite>
                </filter>
              </defs>
              <g
                id="Group_58003"
                data-name="Group 58003"
                transform="translate(-943 -247)"
                data-v-871b8aab=""
                monica-exclude-el="m"
              >
                <g
                  transform="matrix(1, 0, 0, 1, 943, 247)"
                  filter="url(#Ellipse_3512)"
                  data-v-871b8aab=""
                  monica-exclude-el="m"
                >
                  <g
                    id="Ellipse_3512-2"
                    data-name="Ellipse 3512"
                    transform="translate(9 6)"
                    fill="#fff"
                    stroke="#707070"
                    stroke-width="0.8"
                    data-v-871b8aab=""
                    monica-exclude-el="m"
                  >
                    <circle
                      cx="77"
                      cy="77"
                      r="77"
                      stroke="none"
                      data-v-871b8aab=""
                    ></circle>
                    <circle
                      cx="77"
                      cy="77"
                      r="76.6"
                      fill="none"
                      data-v-871b8aab=""
                    ></circle>
                  </g>
                </g>
                <g
                  id="Group_58002"
                  data-name="Group 58002"
                  transform="translate(-4.755 -50.755)"
                  data-v-871b8aab=""
                  monica-exclude-el="m"
                >
                  <g
                    id="user_bold"
                    data-name="user/bold"
                    transform="translate(976.755 323.755)"
                    data-v-871b8aab=""
                    monica-exclude-el="m"
                  >
                    <g
                      id="vuesax_bold_user"
                      data-name="vuesax/bold/user"
                      data-v-871b8aab=""
                      monica-exclude-el="m"
                    >
                      <g id="user" data-v-871b8aab="" monica-exclude-el="m">
                        <path
                          id="Vector"
                          d="M0,0H114.749V114.749H0Z"
                          fill="none"
                          opacity="0"
                          data-v-871b8aab=""
                        ></path>
                        <path
                          id="Vector-2"
                          data-name="Vector"
                          d="M47.812,23.906A23.906,23.906,0,1,1,23.906,0,23.906,23.906,0,0,1,47.812,23.906Z"
                          transform="translate(33.468 9.562)"
                          fill="#a8a8a8"
                          data-v-871b8aab=""
                        ></path>
                        <path
                          id="Vector-3"
                          data-name="Vector"
                          d="M43.461,0C19.507,0,0,16.065,0,35.859A2.367,2.367,0,0,0,2.391,38.25H84.531a2.367,2.367,0,0,0,2.391-2.391C86.922,16.065,67.415,0,43.461,0Z"
                          transform="translate(13.913 69.327)"
                          fill="#a8a8a8"
                          data-v-871b8aab=""
                        ></path>
                      </g>
                    </g>
                  </g>
                </g>
              </g>
            </svg>
            {/* <ProfilePic src="/noAvatar.png" alt="no Avatar" /> */}
          </ProfilePicWrapper>
        </ProfilePicCon>
        <HeaderCon>
          <HeaderMainText>username</HeaderMainText>
          <HeaderSmText>
            <i>email</i>
          </HeaderSmText>
          {/* <HeaderRdmText>
            Lorem ipsum dolor sit amet, consectetur adipisicing elit. Accusamus
            molestias assumenda, distinctio ipsam, praesentium eveniet libero
            esse voluptate reprehenderit unde voluptatibus eius sequi similique
            nihil, quibusdam quidem explicabo incidunt doloremque?
          </HeaderRdmText> */}
        </HeaderCon>
        {resetPass ? (
          <ButtonCon className="flex aic jcc wrap ">
            <Button className="flex aic jcc">
              <span className="flex aic jcc"> Switch to Owner</span>
              <span className="flex aic jcc">{/* <PersonOutline /> */}</span>
            </Button>
            <Button
              style={{ background: "", gap: "3px" }}
              className="flex aic jcc"
              onClick={handleResetPassword}
            >
              <span>Reset Password</span>

              <ButtonSpan className=" flex aic jcc">
                <svg
                  className="w-6 h-6 fill-theme_dark_text"
                  viewBox="0 0 24 24"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <title>Reset Password</title>
                  <path d="M13.16,3.17A8.83,8.83,0,1,1,5.76,16.8l1.4-1.11a7.05,7.05,0,1,0-1-4.57H8.6L5.3,14.41,2,11.12H4.38a8.83,8.83,0,0,1,8.78-7.95m2.57,7.21a.81.81,0,0,1,.81.81v3.9a.82.82,0,0,1-.82.82H11a.79.79,0,0,1-.75-.82V11a.79.79,0,0,1,.74-.81V9.46a2.39,2.39,0,0,1,2.71-2.37A2.47,2.47,0,0,1,15.8,9.57v.81m-1.11-.84A1.22,1.22,0,0,0,14,8.4a1.29,1.29,0,0,0-1.86,1.09v.89h2.57Z" />
                </svg>{" "}
              </ButtonSpan>
            </Button>
          </ButtonCon>
        ) : (
          <ResetPassWrapper>
            <ResetPassTextCon>
              <ResetPassText>Reset your password</ResetPassText>
            </ResetPassTextCon>
            <ResetPassCon>
              {/* <ResetPassInputCon>
                <ResetPassLabelCon id="resetPassOld">
                  Enter your old password
                </ResetPassLabelCon>
                <ResetPassInput id="resetPassOld" className="" />
              </ResetPassInputCon>
              <ResetPassInputCon>
                <ResetPassLabelCon id="resetPassOld">
                  Enter your old password
                </ResetPassLabelCon>
                <ResetPassInput id="resetPassOld" className="" />
              </ResetPassInputCon> */}
              <InputCon>
                <Inputlabel Htmlfor="password">
                  {" "}
                  Enter your old password
                </Inputlabel>
                <InputContainer className="flex aic jcc">
                  {/* <Lock /> */}
                  <Input
                    ref={(el) => PasswordInputRef.current.push(el)}
                    className=""
                    id="password"
                    type="password"
                    placeholder="Enter your old password
"
                    // onChange={(e) => handleSubmit(e, "password")}
                  />
                  {passVisible === 0 ? (
                    <RemoveRedEye
                      sx={{
                        cursor: "pointer",
                        position: "absolute",
                        right: "10px",
                      }}
                      onClick={() => ToggleVisibility(0)}
                      id="visiblePassword_Icon"
                    />
                  ) : (
                    <VisibilityOff
                      sx={{
                        cursor: "pointer",
                        position: "absolute",
                        right: "10px",
                      }}
                      onClick={() => ToggleVisibility(0)}
                      id="visiblePassword_Icon"
                    />
                  )}
                </InputContainer>
              </InputCon>
              <InputCon>
                <Inputlabel Htmlfor="password">
                  {" "}
                  Enter your new password
                </Inputlabel>
                <InputContainer className="flex aic jcc">
                  {/* <Lock /> */}
                  <Input
                    ref={(el) => PasswordInputRef.current.push(el)}
                    className=""
                    id="password"
                    type="password"
                    placeholder="Enter your new password"
                    // onChange={(e) => handleSubmit(e, "password")}
                  />
                  {passVisible === 1 ? (
                    <RemoveRedEye
                      sx={{
                        cursor: "pointer",
                        position: "absolute",
                        right: "10px",
                      }}
                      onClick={() => ToggleVisibility(1)}
                      id="visiblePassword_Icon"
                    />
                  ) : (
                    <VisibilityOff
                      sx={{
                        cursor: "pointer",
                        position: "absolute",
                        right: "10px",
                      }}
                      onClick={() => ToggleVisibility(1)}
                      id="visiblePassword_Icon"
                    />
                  )}
                </InputContainer>
              </InputCon>
            </ResetPassCon>
            <Back className=" aic jcc">
              <BackBtn className="flex aic jcfs">
                <div className="flex aic jcfs  w100 ">
                  <KeyboardDoubleArrowLeft sx={{fontSize:"1.2rem"}} />
                </div>
                <div className="flex aic jcfs w100 "> Back</div>
              </BackBtn>
            </Back>
          </ResetPassWrapper>
        )}
      </Wrapper>
       
    </Container>
  );
};

export default Home;
