import { useEffect, useState } from "react";
import styled from "styled-components";
import {
  AccountCircle,
  FacebookRounded,
  Google,
  PersonRounded,
} from "@mui/icons-material";
import { Link, useLocation, useNavigate } from "react-router-dom";
const Container = styled.div`
  width: 100vw;
  height: 100vh;
  background: #dbeafe;
  color: #3c3c3c;
`;
const Wrapper = styled.div`
  width: 100%;
  // height: 65%;
`;

const Header = styled.div`
  width: 100%;
  padding-bottom: 8px;
`;
const HeaderImgCon = styled.div`
  max-width: 30px;
  margin-right: 4px;
`;
const HeaderImg = styled.img`
  object-fit: contain;
  width: 100%;
`;
const HeaderTextCon = styled.div``;
const HeaderText = styled.span`
  font-weight: 900;
  font-size: 20px;
`;
const RegisterCon = styled.form`
  width: 100%;
  height: 100%;
  background: white;
  padding: 25px;
  border-radius: 5px;
  box-shadow: 0px 0px 15px -10px rgba(0, 0, 0, 0.75);
`;

const RegisterTextCon = styled.div`
  padding-bottom: 7px;
`;
const RegisterText = styled.span`
  font-weight: 900;
  font-size: 18px;
  // color:#4d5572;
  // color:#3c3c3c;
`;

const InputWrapper = styled.div`
  display: grid;
  grid-gap: 20px;
  grid-template-columns: 1fr 1fr;
`;
const InputCon = styled.div``;
const Input = styled.input`
  width: 100%;
  // height: 100%;
  padding: 7px;
  border: none;
  outline: none;
  background: transparent;
  border: 1px solid #c5c5c5;
  border-radius: 6px;
  box-sizing: border-box;

  &::placeholder {
    font-size: 12px;
  }
`;
const Inputlabel = styled.label`
  font-size: 13px;
  font-weight: 600;
`;

const CheckboxCon = styled.div`
  margin: 15px 0;
`;
const Checkbox = styled.input``;
const CheckboxLabel = styled.label`
  font-size: 13px;
  font-weight: 600;
  margin-left: 5px;
`;
const CheckboxLabelB = styled.span`
  color: #2563eb;
`;

const SubmitButton = styled.button`
  background: #1e5eeb;
  width: 100%;
  padding: 10px 0;
  border: none;
  color: white;
  border-radius: 8px;
  font-weight: 600;
  cursor: pointer;
`;

const Hr = styled.hr`
  background: #c5c5c5;
  border: none;
  width: 100%;
  height: 1px;
  margin-top: 20px;
`;

const SignUpOptions = styled.div``;
const SignUpOptionsHeader = styled.div`
  padding: 7px 0;
`;
const SignUpOptionsHeaderText = styled.span`
  font-size: 11.6px;
  font-weight: 900;
  color: grey;
`;
const SignUpOptionsButtonCon = styled.div`
  gap: 20px;
`;
const SignUpOptionsButton = styled.button`
  background: #eee;
  border-radius: 8px;
  padding: 6px 30px;
  font-size: 12px;
  font-weight: 600;
  border: none;
`;
const SignUpOptionsIcon = styled.div`
  margin-right: 7px;
`;

const Redirect = styled.div`
  padding: 8px 0;
`;
const Links = styled.span`
  font-size: 14px;
  font-weight: 500;
`;
const LinksBl = styled.span`
  color: #2563eb;
  font-weight: 600;
  margin-left: 5px;
`;

const ErrorCon = styled.div`
  margin: 6px 0;
`;
const ErrorText = styled.p`
  color: red;
  font-size: 14px;
  text-transform: capitalize;
`;

const LabelCon = styled.div`
