              <span>Upload a file</span>
              <InputFile
                type="file"
                name="avatar"
                id="file-input"
                accept=".jpg,.jpeg,.png"
                // onChange={handleFileInputChange}
                className="sr-only"
              />
            </InputLabel>
          </LabelCon>
          <InputWrapper>
            <InputCon>
              <Inputlabel Htmlfor="name">Your Name</Inputlabel>
              <Input
                id="name"
                placeholder="your name"
                onChange={(e) => handleSubmitInputChange(e, "name")}
