  }

  return (
    <Container className="flex aic jcc">
      <Wrapper className="flex aic jcc fdc">
        <Header className="w100 flex aic jcc fdc">
          <HeaderMainText>
            Please enter the One-Time Password to verify your account
          </HeaderMainText>
          <HeaderSmText>
            A one time password has been sent to email
          </HeaderSmText>
        </Header>
        <OTPInput
          value={otp}
          onChange={setOtp}
          numInputs={6}
          renderSeparator={<span> </span>}
          renderInput={(props) => <OtpPinsInput {...props} />}
        />
        <OtpPinCon className="none aic jcc ">
          <OtpPins>
            <OtpPinsInput maxLength="1" type="number" />
          </OtpPins>
          <OtpPins>
            <OtpPinsInput max={1} type="number" />
          </OtpPins>
          <OtpPins>
            <OtpPinsInput type="number" />
          </OtpPins>
          <OtpPins>
            <OtpPinsInput type="number" />
          </OtpPins>
          <OtpPins>
            <OtpPinsInput type="number" />
          </OtpPins>
        </OtpPinCon>
        <ButtonCon className="flex aic jcc">
          <Button type="submit">VERIFY</Button>
        </ButtonCon>
      </Wrapper>
    </Container>
  );
};

export default VerifyOTP;
