import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { tablet } from "../responsive";
import styled from "styled-components";
import OTPInput from "react-otp-input";
import { useState } from "react";
import axios from "axios";
const Container = styled.div`
  width: 100vw;
  height: 100dvh;
  background: #dbeafe;
  color: #3c3c3c;
`;
const Wrapper = styled.div`
  background: white;
  border-radius: 7px;
  padding: 20px;
  gap: 1rem;
  //   width: 30%;
  // height: 65%;
  ${tablet({ width: "80%" })};
`;

const Header = styled.div``;
const HeaderMainText = styled.h3`
  font-weight: 700;
  text-align: center;
`;
const HeaderSmText = styled.p``;

const OtpPinCon = styled.div`
  gap: 3rem;
`;
const OtpPins = styled.div`
  height: 3rem;
  width: 3rem;
`;
const OtpPinsInput = styled.input`
  outline: none;
  height: 100%;
  width: 100%;
  border: none;
  border-bottom: 2px solid grey;
  margin: 0 10px;
  font-size: 30px;
`;

const ButtonCon = styled.div``;
const Button = styled.button`
  background: #2563eb;
  padding: 0.5rem 2.5rem;
  border-radius: 4px;
  color: white;
  border: none;
  outline: none;
  font-size: 17px;
  font-weight: 700;
`;

const VerifyOTP = () => {
  const [otp, setOtp] = useState("");
  const [changeEmail,setChangeEMail]=useState(false)
  const handleSubmit = async ()=>{
    try{
      const res = await axios.get("https://industrialiot.onrender.com/api/verifyemail",{
        email:"chinonsoxavier26@gmail.com"
      });
      console.log(res)
    }catch(err){
      console.log(err)
    }
  }

  return (
    <Container className="flex aic jcc">
      <Wrapper className="flex aic jcc fdc">
        <Header className="w100 flex aic jcc fdc">
          <HeaderMainText>
            Please enter the One-Time Password to verify your account
          </HeaderMainText>
          <HeaderSmText>
            A one time password has been sent to email
          </HeaderSmText>
        </Header>
        <OTPInput
          value={otp}
          onChange={setOtp}
          numInputs={6}
          renderSeparator={<span> </span>}
          renderInput={(props) => <OtpPinsInput {...props} />}
        />
        
        <ButtonCon className="flex aic jcc">
          <Button className="cp" type="submit" onClick={handleSubmit}>
            VERIFY
          </Button>
        </ButtonCon>
        <div>
          <div>
            <p>Didn`t recieve otp? send again</p>
            <p>change email</p>
          </div>
        </div>
      </Wrapper>
    </Container>
  );
};

export default VerifyOTP;
