import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { tablet } from "../responsive";
const Container = styled.div`
  width: 100vw;
  height: 100dvh;
  background: #dbeafe;
  color: #3c3c3c;
`;
const Wrapper = styled.div`
  width: 30%;
  // height: 65%;
  ${tablet({ width: "80%" })};
`;
const RegisterCon = styled.form`
  width: 100%;
  height: 100%;
  background: white;
  padding: 25px;
  border-radius: 5px;
  box-shadow: 0px 0px 15px -10px rgba(0, 0, 0, 0.75);
`;

const RegisterTextCon = styled.div`
  padding-bottom: 7px;
`;
const RegisterText = styled.p`
  font-weight: 600;
  font-size: 25px;
  // color:#4d5572;
  // color:#3c3c3c;
`;

const InputWrapper = styled.div`
  display: grid;
  grid-gap: 20px;
  grid-template-columns: 1fr;
  background: white;
`;
const InputCon = styled.div`
  background: white;
`;
const Input = styled.input`
  background: white;
  width: 100%;
  // height: 100%;
  padding: 7px;
  border: none;
  outline: none;
  border: 1px solid #eee;
  border-radius: 6px;
  box-sizing: border-box;

  &:focus {
    background: #eee;
  }

  &::placeholder {
    font-size: 12px;
  }
`;

const Inputlabel = styled.label`
  font-size: 15px;
  font-weight: 600;
  background: white !important;
  margin: 7px 0;
`;
const InputContainer = styled.div`
  border: 1px solid #eee;
  flex: 1;
  // padding: 0 30px;
  // height: 60px;
  color: grey;
  position: relative;
  // margin: 20px 0;
`;

const SubmitButton = styled.button`
  background: #1e5eeb;
  width: 100%;
  padding: 10px 0;
  border: none;
  color: white;
  border-radius: 8px;
  font-weight: 600;
  cursor: pointer;
  margin: 25px 0;

  &:disabled {
    cursor: not-allowed;
  }
`;

const Hr = styled.hr`
  background: #c5c5c5;
  border: none;
  width: 100%;
  height: 1px;
  margin-top: 10px;
`;

const SignUpOptions = styled.div``;
const SignUpOptionsHeader = styled.div`
  padding: 7px 0;
`;
const SignUpOptionsHeaderText = styled.span`
  font-size: 11.6px;
  font-weight: 600;
  color: grey;
`;
const SignUpOptionsButtonCon = styled.div`
  gap: 20px;
`;
const SignUpOptionsButton = styled.button`
  background: #eee;
  border-radius: 8px;
  padding: 6px 30px;
  font-size: 12px;
  font-weight: 600;
  border: none;
`;
const SignUpOptionsIcon = styled.div`
  margin-right: 7px;
`;

const Redirect = styled.div`
  padding: 8px 0;
`;
const Links = styled.span`
  font-size: 14px;
  font-weight: 600;
`;
const LinksBl = styled.span`
  color: #2563eb;
  font-weight: 600;
  margin-left: 5px;
`;

const ErrorMessage = styled.div`
  background: #e04e4e;
`;
const ErrorMessageText = styled.p`
  color: white;
  font-weight: 500;
  text-align: center;
`;
const MinPassErrorCon = styled.div`
  background: #f4ddde;
`;
const MinPassErrorText = styled.p`
  color: #bc8d8a;
  font-weight: 300;
  text-align: center;
  font-size: 13px;
`;

const VerifyOTP = () => {
 
  return (
    <Container className="flex aic jcc">
     <Wrapper>

     </Wrapper>
    </Container>
  );
};

export default VerifyOTP;
