import { useEffect, useMemo, useRef, useState } from "react";
import styled from "styled-components";
import {
  FacebookRounded,
  Google,
  Lock,
  RemoveRedEye,
  VisibilityOff,
} from "@mui/icons-material";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import { login } from "../redux/apiCalls";
import { useDispatch, useSelector } from "react-redux";
import {
  sessionFailure,
  sessionStart,
  sessionSucess,
} from "../redux/themeSlice";
import { apiRoute } from "../utils/apiRoutes";
import { tablet } from "../responsive";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
const Container = styled.div`
  width: 100vw;
  height: 100dvh;
  background: #dbeafe;
  color: #3c3c3c;
`;
const Wrapper = styled.div`
  width: 30%;
  // height: 65%;
  ${tablet({ width: "80%" })};
`;

const Header = styled.div`
  width: 100%;
  padding-bottom: 8px;
`;
const HeaderImgCon = styled.div`
  max-width: 30px;
  margin-right: 4px;
`;
const HeaderImg = styled.img`
  object-fit: contain;
  width: 100%;
`;
const HeaderTextCon = styled.div``;
const HeaderText = styled.span`
  font-weight: 900;
  font-size: 20px;
`;
const RegisterCon = styled.form`
  width: 100%;
  height: 100%;
  background: white;
  padding: 25px;
  border-radius: 5px;
  box-shadow: 0px 0px 15px -10px rgba(0, 0, 0, 0.75);
`;

const RegisterTextCon = styled.div`
  padding-bottom: 7px;
`;
const RegisterText = styled.p`
  font-weight: 600;
  font-size: 25px;
  // color:#4d5572;
  // color:#3c3c3c;
`;

const InputWrapper = styled.div`
  display: grid;
  grid-gap: 20px;
  grid-template-columns: 1fr;
  background: white;
`;
const InputCon = styled.div`
  background: white;
`;
const Input = styled.input`
  background: white;
  width: 100%;
  // height: 100%;
  padding: 7px;
  border: none;
  outline: none;
  border: 1px solid #eee;
  border-radius: 6px;
  box-sizing: border-box;

  &:focus {
    background: #eee;
  }

  &::placeholder {
    font-size: 12px;
  }
`;

const Inputlabel = styled.label`
  font-size: 15px;
  font-weight: 600;
  background: white !important;
  margin: 7px 0;
`;
const InputContainer = styled.div`
  border: 1px solid #eee;
  flex: 1;
  // padding: 0 30px;
  // height: 60px;
  color: grey;
  position: relative;
  // margin: 20px 0;
`;

const SubmitButton = styled.button`
  background: #1e5eeb;
  width: 100%;
  padding: 10px 0;
  border: none;
  color: white;
  border-radius: 8px;
  font-weight: 600;
  cursor: pointer;
  margin: 25px 0;

  &:disabled {
    cursor: not-allowed;
  }
`;

const Hr = styled.hr`
  background: #c5c5c5;
  border: none;
  width: 100%;
  height: 1px;
  margin-top: 10px;
`;

const SignUpOptions = styled.div``;
const SignUpOptionsHeader = styled.div`
  padding: 7px 0;
`;
const SignUpOptionsHeaderText = styled.span`
  font-size: 11.6px;
  font-weight: 600;
  color: grey;
`;
const SignUpOptionsButtonCon = styled.div`
  gap: 20px;
`;
const SignUpOptionsButton = styled.button`
  background: #eee;
  border-radius: 8px;
  padding: 6px 30px;
  font-size: 12px;
  font-weight: 600;
  border: none;
`;
const SignUpOptionsIcon = styled.div`
  margin-right: 7px;
`;

const Redirect = styled.div`
  padding: 8px 0;
`;
const Links = styled.span`
  font-size: 14px;
  font-weight: 600;
`;
const LinksBl = styled.span`
  color: #2563eb;
  font-weight: 600;
  margin-left: 5px;
`;

const ErrorMessage = styled.div`
  background: #e04e4e;
`;
const ErrorMessageText = styled.p`
  color: white;
  font-weight: 500;
  text-align: center;
`;
const MinPassErrorCon = styled.div`
  background: #f4ddde;
`;
const MinPassErrorText = styled.p`
  color: #bc8d8a;
  font-weight: 300;
  text-align: center;
  font-size: 13px;
`;

const Register = () => {
  const [Password, setPassword] = useState("");
  const [Email, setEmail] = useState();
  const [error, setError] = useState();
  const [minPass, setMinPass] = useState(0);
  const [allowLogin, setAllowLogin] = useState(0);
  const [passwordError, setPasswordError] = useState(null);
  const PasswordInputRef = useRef(null);
  const EmailInputRef = useRef(null);
  const [passVisible, setPassVisible] = useState(false);
  // const passVisible = []
  // const [passVisible,setPassvisible]=useState=(false)

  const dispatch = useDispatch();
  var userSession = {};
  const user = { email: Email, password: Password };
  const memoizedUser = useMemo(() => user, []);
  const currentUser = useSelector((state) => state.Store.User.currentUser);
  const navigate = useNavigate();
  currentUser && navigate("/");

  function validatePassword(password) {
    // Regular expression pattern for a strong password
    const strongPasswordRegex =
      /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*#?&])[8,32]$/;
    return strongPasswordRegex.test(password);
  }
  const handleSubmit = (e, type) => {
    e.preventDefault();
    if (type === "Email") {
      setEmail(e.target.value);
      setAllowLogin((prevValue) => prevValue + 1);
      console.log(type, Email);
    } else {
      setPassword(e.target.value);
      setMinPass((prev) => (prev += 1));
      setAllowLogin((prevValue) => prevValue + 1);

      console.log(type, Password);
    }
    // alert
  };

  const ErrorAlert = (message) =>
    toast.error(message, {
      bodyClassName: "ToastifyLoginError",
      position: "bottom-center",
      fontSize: "2px",
      autoClose: 5000,
      hideProgressBar: false,
      newestOnTop: true,
      closeOnClick: true,
      pauseOnFocusLoss: true,
      draggable: true,
      pauseOnHover: true,
      theme: "light",
      rtl: false,
    });
  // ErrorAlert();
  const ToggleVisibility = () => {
    const passwordDisplayType = PasswordInputRef.current.type;
    if (passwordDisplayType === "password") {
      PasswordInputRef.current.type = "test";
      setPassVisible(true);
    } else {
      PasswordInputRef.current.type = "password";
      setPassVisible(false);
    }
    // const type =  passwordDisplayType === "password"? passwordDisplayType === "text": passwordDisplayType==='password';
    // return type;
  };
  const handleClick = async (e) => {
    e.preventDefault();
    // if (!validatePassword(Password)) {
    //   PasswordInputRef.current.value = "";
    //   PasswordInputRef.current.border = "1px solid red";
    //   PasswordInputRef.current.background = " red";
    //   ErrorAlert(
    //     "Password must at least 8 characters long and contains at least one lowercase letter,one upercase letter,one number,and one special character."
    //   );
    // } else {
      sessionStart();
      try {
        const User = await axios.post(apiRoute + "/auth/login", user);
        dispatch(sessionSucess({ currentUser: User.data }));
        userSession = User.data;
        localStorage.setItem("user", JSON.stringify(userSession));
        console.log(currentUser);

        console.log(User.data);
      } catch (err) {
        if (error) {
          PasswordInputRef.current.value = "";
        } else {
          PasswordInputRef.current.value = "";
        }
        ErrorAlert(
          "We could`nt find an account matching the email and password you entered.Please check your email and password and try again."
        );
        console.log(err);
        setError(err.response.data);
        console.log(error);
        sessionFailure();
      
    }
  };

  useEffect(() => {
    setEmail(Email);
    setPassword(Password);
    console.log(user);
  }, [Email, Password, memoizedUser]);

  return (
    <Container className="flex aic jcc">
      <Wrapper className="flex aic jcc fdc">
        <ToastContainer
          position="bottom-center"
          autoClose={5000}
          hideProgressBar={false}
          newestOnTop={true}
          closeOnClick={true}
          pauseOnFocusLoss={true}
          draggable
          pauseOnHover
          theme="light"
          rtl={false}
          fontSize="2px"
        />
        <Header className="flex aic jcc">
          <HeaderImgCon className="flex aic jcc">
            <HeaderImg src={require(".././utils/images/logo.png")} />
          </HeaderImgCon>
          <HeaderTextCon className="flex aic jcc v">
            <HeaderText>Connect Me</HeaderText>
          </HeaderTextCon>
        </Header>
        <RegisterCon>
          {/* <ErrorMessage className="flex aic jcc">
            <ErrorMessageText>
              We could`nt find an account matching the email and password you
              entered.Please check your email and password and try again.
            </ErrorMessageText>
          </ErrorMessage> */}
          <RegisterTextCon>
            <RegisterText>Login</RegisterText>
          </RegisterTextCon>
          <InputWrapper>
            <InputCon className="">
              <Inputlabel Htmlfor="name">Email Address</Inputlabel>
              <Input
                ref={EmailInputRef}
                className=""
                id="Address"
                placeholder="Email Address"
                onChange={(e) => handleSubmit(e, "Email")}
              />
            </InputCon>
            <InputCon>
              <Inputlabel Htmlfor="password">Password</Inputlabel>
              <InputContainer className="flex aic jcc">
                {/* <Lock /> */}
                <Input
                  ref={PasswordInputRef}
                  className=""
                  style={{
                    border: error && "1px solid red",
                  }}
                  error={error}
                  id="password"
                  type="password"
                  placeholder="password"
                  onChange={(e) => handleSubmit(e, "password")}
                />
                {!passVisible ? (
                  <VisibilityOff
                    sx={{
                      cursor: "pointer",
                      position: "absolute",
                      right: "10px",
                    }}
                    onClick={ToggleVisibility}
                    id="visiblePassword_Icon"
                  />
                ) : (
                  <RemoveRedEye
                    sx={{
                      cursor: "pointer",
                      position: "absolute",
                      right: "10px",
                    }}
                    onClick={ToggleVisibility}
                    id="visiblePassword_Icon"
                  />
                )}
              </InputContainer>
              {passwordError && (
                <MinPassErrorCon>
                  <MinPassErrorText>{passwordError}</MinPassErrorText>
                </MinPassErrorCon>
              )}
            </InputCon>
          </InputWrapper>
          <SubmitButton
            onClick={handleClick}
            type="submit"
            disabled={Password === ""}
          >
            Log in
          </SubmitButton>
          <Hr />
          <SignUpOptions className="flex aic jcc fdc">
            <SignUpOptionsHeader className="flex aic jcc ">
              <SignUpOptionsHeaderText>OR USE WITH</SignUpOptionsHeaderText>
            </SignUpOptionsHeader>
            <SignUpOptionsButtonCon className="flex aic jcc">
              <SignUpOptionsButton className="flex aic jcc">
                <SignUpOptionsIcon className="flex aic jcc">
                  <Google sx={{ fontSize: "18px" }} />
                </SignUpOptionsIcon>
                Google
              </SignUpOptionsButton>
              <SignUpOptionsButton className="flex aic jcc">
                <SignUpOptionsIcon className="flex aic jcc">
                  <FacebookRounded sx={{ fontSize: "18px" }} />
                </SignUpOptionsIcon>
                Facebook
              </SignUpOptionsButton>
            </SignUpOptionsButtonCon>
          </SignUpOptions>
        </RegisterCon>
        <Redirect>
          <Links className="flex aic jcc">
            Don`t` have an account?
            <LinksBl>
              <Link to="/register" className="link">
                Register
              </Link>
            </LinksBl>
          </Links>
        </Redirect>
      </Wrapper>
    </Container>
  );
};

export default Register;
