import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { Laptop, Lmobile, SLaptop, tablet } from "../responsive";
import styled from "styled-components";
import axios from "axios";
import { Link } from "react-router-dom";
import { Close, Menu } from "@mui/icons-material";
import { useState } from "react";
import { useContext } from "react";
import { UserContext } from "../context/context";

const Con = styled.div`
  position: relative;
  width: 100vw;
  height: 100dvh;
  background: #dbeafe;
`;

const Container = styled.div`
  width: 100vw;
  height: 100dvh;
  background: #dbeafe;
  color: black;
`;
const Wrapper = styled.div`
  background: white;
  border-radius: 7px;
  padding: 20px;
  gap: 1rem;
  width: 30%;
  // height: 65%;
  ${Laptop({ width: "50%" })};
  ${tablet({ width: "80%" })};
`;

const Navbar = styled.div`
  position: absolute;
  top: 0;
  width: 100%;
  // background:red;
  padding: 30px 50px;
  box-sizing: border-box;
  z-index: 999;
  // border-bottom:1px solid white;
  box-shadow: 0px 0px 5px rgba(0, 0, 0, 0.3);
`;
const NavbarLeft = styled.div`
  flex: 1;
  font-size: 25px;
  font-weight: 600;
  ${Lmobile({ fontSize: "20px" })};
`;
const NavbarMiddle = styled.div`
  gap: 1rem;
  flex: 3;
  display: flex;
  ${tablet({ display: "none" })};
`;
const NavbarRight = styled.div`
  flex: 1;
  gap: 1rem;
  ${SLaptop({ display: "none" })};
`;

const NavbarTextCon = styled.div``;

const NavbarText = styled.p`
  white-space: nowrap;
  font-size: 0.9rem;
  cursor: pointer;
`;

const NavbarMenu = styled.div`
  display: none;
  font-size: 35px;
  cursor: pointer;
  ${Lmobile({ fontSize: "25px" })};
  ${tablet({ display: "flex" })};
`;

const NavbarButton = styled.button`
  background: ${(props) => (props.outline ? " transparent" : "#2563eb")};
  border: ${(props) => (props.outline ? " 1px solid #2563eb" : "none")};
  color: ${(props) => (props.outline ? " black" : "white")};
  padding: 10px 20px;
  flex: 1;
  border-radius: 30px;
  flex-basis: 10px;
  max-width: 240px;
  cursor: pointer;
  font-weight: 700;
`;

const ProfilePicCon = styled.div``;
const ProfilePicWrapper = styled.div`
  max-width: 200px;
  max-height: 200px;
  border-radius: 50%;
  min-width: 0;
  min-height: 0;
  // border: 1px solid gray;
  ${tablet({ maxWidth: "100px", maxHeight: "100px" })};
`;
const HeaderCon = styled.div``;
const HeaderMainText = styled.h2`
  font-weight: 700;
  color: black;
  text-align: center;
`;
const HeaderSmText = styled.p`
  font-size: 0.99rem;
  text-align: center;
`;
const ResetPasswordCon = styled.div`
  width: 100%;
  text-align: center;
  width: 100%;
  position: absolute;
  bottom: 20px;
  // background:red;
  padding: 0 50px;
  box-sizing: border-box;
  // margin:25px 0;
`;
const ResetPassword = styled.div`
  cursor: pointer;
`;
const ResetPasswordBdText = styled.span`
  margin: 0 2px;
  font-size: 0.99rem;
`;
const DeleteAccount = styled.div`
  background: red;
  cursor: pointer;
  color: white;
  padding: 10px;
  border-radius: 9px;
  ${tablet({ padding: "6px" })};
`;
const DeleteAccountBdText = styled.span`
  margin: 0 2px;
  font-size: 0.99rem;
`;

const ButtonCon = styled.div`
  gap: 10px;
`;
const Button = styled.button`
  background: #2563eb;
  padding: 0.6rem 2.2rem;
  border-radius: 4px;
  color: white;
  border: none;
  outline: none;
  font-size: 15px;
  font-weight: 600;
  cursor: pointer;

  &:hover {
    transform: scale(1.05);
    transition: 0.4s all;
  }
`;

const ButtonSpan = styled.span`
  font-size: 20px;
`;

const SideMenuCon = styled.div`
  position: absolute;
  left: 0;
  z-index: 99999;
  width: 0;
  height: 100dvh;
  background: rgba(255, 255, 255, 0.9);
  background: whitesmoke;
  top: 0;
  overflow: hidden;
  text-slign: center;
  gap: 1rem;
`;

const SideMenuButton = styled.button`
  background: ${(props) => (props.outline ? " transparent" : "#2563eb")};
  border: ${(props) => (props.outline ? " 1px solid #2563eb" : "none")};
  color: ${(props) => (props.outline ? " black" : "white")};
  padding: 10px 20px;
  // flex: 1;
  border-radius: 30px;
  flex-basis: 10px;
  max-width: 240px;
  cursor: pointer;
  font-weight: 700;
  width: 100%;
`;

const UsersList = () => {
  const user = useContext(UserContext);
  const [sideMenuOpen, setSideMenuOpen] = useState(false);

 const SideMenu = () => {
    return (
      <SideMenuCon
        className="flex aic jcc fdc"
        style={{ width: sideMenuOpen ? "75%" : "0" }}
      >
        <NavbarText>
          <Link to="/reset-password" className="link">
            Reset Password
          </Link>
        </NavbarText>
        <NavbarText>
          <Link to="/users-list" className="link">
            Get all users
          </Link>
        </NavbarText>
        <NavbarText>Switch to owner</NavbarText>
        {/* <NavbarText>Delete account</NavbarText> */}
        <NavbarText>Logout</NavbarText>
        {user.accessToken ? (
          <SideMenuButton>LOGOUT</SideMenuButton>
        ) : (
          <>
            <SideMenuButton outline>
              <Link to="register" className="link">
                Register
              </Link>
            </SideMenuButton>
            <SideMenuButton>
              <Link to="login" className="link">
                Login
              </Link>
            </SideMenuButton>
          </>
        )}
      </SideMenuCon>
    );
  };

  const handleUpdateRole = async () => {
    try {
      const res = await axios.post("");
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <Con>
      <Navbar className="flex aic jcsb w100">
        <NavbarLeft>
          <p style={{ fontSize: "inherit" }}>Industrial IOT</p>
        </NavbarLeft>
        <NavbarMiddle className="aic jcc">
          <NavbarText>
            <Link to="/reset-password" className="link">
              Reset Password
            </Link>
          </NavbarText>
          <NavbarText>
            <Link to="/users-list" className="link">
              Get all users
            </Link>
          </NavbarText>
          <NavbarText>Switch to owner</NavbarText>
          <NavbarText>Delete account</NavbarText>
          {/* <NavbarText>Logout</NavbarText> */}
        </NavbarMiddle>
        <NavbarRight className="flex aic jcfe">
          {!user.accessToken ? (
            <>
              <NavbarButton outline>
                <Link to="/register" className="link">
                  REGISTER
                </Link>
              </NavbarButton>
              <NavbarButton>
                <Link to="/login" className="link">
                  Login
                </Link>
              </NavbarButton>
            </>
          ) : (
            <NavbarButton>LOGOUT</NavbarButton>
          )}
        </NavbarRight>
        <NavbarMenu className="flex aic jcc">
          {!sideMenuOpen ? (
            <Menu
              onClick={() => setSideMenuOpen((current) => !current)}
              sx={{ fontSize: "inherit" }}
            />
          ) : (
            <Close
              onClick={() => setSideMenuOpen((current) => !current)}
              sx={{ fontSize: "inherit" }}
            />
          )}
        </NavbarMenu>
      </Navbar>
      <Container className="flex aic jcc fdc">
        <Wrapper className="flex aic jcc fdc bgw">
          
      </Wrapper>
      </Container>
      <SideMenu />
    </Con>
  );
};

export default UsersList;
