import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { tablet } from "../responsive";
import styled from "styled-components";
const Container = styled.div`
  width: 100vw;
  height: 100dvh;
  background: #dbeafe;
  color: #3c3c3c;
`;
const Wrapper = styled.div`
background:white;
border-radius:7px;
padding:20px;
  width: 30%;
  // height: 65%;
  ${tablet({ width: "80%" })};
`;

const Header = styled.div``
const HeaderMainText = styled.h2`
font-weight:700;
`;
const HeaderSmText = styled.p``;

const OtpPinCon = styled.div``
const OtpPins = styled.div``
const OtpPinsInput = styled.div``

const VerifyOTP = () => {
 
  return (
    <Container className="flex aic jcc">
     <Wrapper>
     <Header className="w100 flex aic jcc fdc" >
        <HeaderMainText>Verify</HeaderMainText>
        <HeaderSmText>your verification code has been sent to your email</HeaderSmText>
     </Header>
     <OtpPinCon className="flex aic jcc" 
     ></OtpPinCon>
     </Wrapper>
    </Container>
  );
};

export default VerifyOTP;
