import styled from "styled-components"

const Container = styled.div` 
width:100dvw;
height:100dvh;
background:#fefaf1;
`
const Wrapper = styled.div`
max-width:30%;
// background:red;
`
const Header = styled.div``
const HeaderBgText = styled.p`
  font-size: 2.2rem;
  font-weight: 500;
  text-align: center;
`;
const HeaderSmText = styled.p`
text-align:center;
font-weight:100;
font-size:.85rem;
opacity:.8;
`;

const InputCon = styled.div`
width:100%;
`
const InputWrapper = styled.div`
  width: 100%;
  border: 1px solid grey;
  height: 100%;
  background: transparent;
  margin: 5px 0;
`;
const InputLabel = styled.label`
`;
const Input = styled.input`
  width: 100%;
  height: 100%;
  border: none;
  outline: none;
  color: gray;
  background: transparent;
  padding:2px 7px;
`;

const ButtonCon = styled.div``
const Button = styled.button`
  background:${props=>props.type!=='outline'?"transparent":"green"};
  padding:${props=>props.type!=='outline'?"4px 35px":"4px 8px"};
  border:none;
  
`

function ResetPassword() {
  return (
    <Container className="flex aic jcc">
      <Wrapper className="flex aic jcc fdc">
        <Header>
          <HeaderBgText>Reset Your Password</HeaderBgText>
          <HeaderSmText>
            Input your account email and we`ll send an OTP to your Email,To
            reset your password!.
          </HeaderSmText>
        </Header>
        <InputCon>
          <InputLabel htmlFor="resetPassword">Email</InputLabel>
          <InputWrapper>
          <Input placeholder="Enter your account email" type="email" id="resetPassword" className="" />
          </InputWrapper>
        </InputCon>
        <ButtonCon>
          <Button>
            Reset PAssword
          </Button>
          <Button type="outline" style={{textDecoration:'underline'}} >
            Return to login
          </Button>
        </ButtonCon>
      </Wrapper>
    </Container>
  );
}

export default ResetPassword
