import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { tablet } from "../responsive";
import styled from "styled-components";
import OTPInput from "react-otp-input";
import { useState } from "react";
import axios from "axios";
import { useContext } from "react";
import { UserContext } from "../context/context";
import { useEffect } from "react";
import { DeleteOutline } from "@mui/icons-material";
const Container = styled.div`
  width: 100vw;
  height: 100dvh;
  background: #dbeafe;
  color: black;
  position:relative;
`;
const Wrapper = styled.div`
  background: white;
  border-radius: 7px;
  padding: 20px;
  gap: 1rem;
    width: 30%;
  // height: 65%;
  ${tablet({ width: "80%" })};
`;

const ProfilePicCon = styled.div``;
const ProfilePicWrapper = styled.div`
  max-width: 200px;
  max-height: 200px;
  border-radius: 50%;
  min-width: 0;
  min-height: 0;
  border: 1px solid gray;
  ${tablet({ maxWidth: "100px",maxHeight:"100px" })};
`;
const ProfilePic = styled.img`
  height: 100%;
  border-radius: 50%;
  width: 100%;
  object-fit: contain;
`;
const HeaderCon = styled.div``
const HeaderMainText = styled.h2`
  font-weight: 700;
  color: black;
  text-align: center;
`;
const HeaderSmText = styled.p`
  font-size: 0.99rem;
  text-align: center;
`;
const HeaderRdmText = styled.p`
  font-size: 0.79rem;
  text-align: center;
  text-overflow: ellipsis;
  overflow: hidden;
  display: inline-block;
//   max-width: 10px;
`;

const ResetPasswordCon = styled.div`
  width: 100%;
  text-align: center;
  width: 100%;
  position: absolute;
  bottom: 30px;
    padding:0 50px;
  box-sizing: border-box;
//   background: red;
`;
const ResetPassword = styled.div`
cursor:pointer;
`;
const ResetPasswordBdText = styled.span`
margin: 0 2px;
  font-size: 0.99rem;
`;
const DeleteAccountCon = styled.div`
  width: 100%;
  text-align: center;
  width: 100%;
  position: absolute;
  bottom:30px;
  padding:0 20px;
  box-sizing:border-box;
  `;
  const DeleteAccount = styled.div`
  background:red;
cursor:pointer;
color:white;
padding:10px;
border-radius:9px;
`;
const DeleteAccountBdText = styled.span`
  margin: 0 2px;
  font-size: 0.99rem;
`;

const ChangeEmailCon = styled.div`
  width: 60%;
`;
const ChangeEmailInput = styled.input`
  width: 100%;
  height: 100%;
  outline: none;
  border: none;
  border-bottom: 1px solid grey;
  // border-radius: 5px;
  padding: 5px 3px;
  transition: 0.5s all;

  &:focus {
    border-bottom: 1.8px solid #2563eb;
  }
`;
const ChangeEmailButton = styled.div``;


const ButtonCon = styled.div`
   gap:10px;
`;
const Button = styled.button`
  background: #2563eb;
  padding: 0.6rem 2.2rem;
  border-radius: 4px;
  color: white;
  border: none;
  outline: none;
  font-size: 15px;
  font-weight: 600;
  cursor:pointer;

  &:hover{
    transform:scale(1.05);
    transition:.4s all;
  }
`;

const ButtonSpan = styled.span`
  font-size:20px;
`

const Home = () => {
 

  return (
    <Container className="flex aic jcc">
      <Wrapper className="flex aic jcc fdc bgw">
        <ProfilePicCon className="flex aic jcc w100">
          <ProfilePicWrapper className="flex aic jcc">
            <ProfilePic src="/noAvatar.png" alt="no Avatar" />
          </ProfilePicWrapper>
        </ProfilePicCon>
        <HeaderCon>
          <HeaderMainText>username</HeaderMainText>
          <HeaderSmText>
            <i>email</i>
          </HeaderSmText>
          <HeaderRdmText>
            Lorem ipsum dolor sit amet, consectetur adipisicing elit. Accusamus
            molestias assumenda, distinctio ipsam, praesentium eveniet libero
            esse voluptate reprehenderit unde voluptatibus eius sequi similique
            nihil, quibusdam quidem explicabo incidunt doloremque?
