import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { tablet } from "../responsive";
import styled from "styled-components";
import OTPInput from "react-otp-input";
import { useState } from "react";
import axios from "axios";
const Container = styled.div`
  width: 100vw;
  height: 100dvh;
  background: #dbeafe;
  color: #3c3c3c;
`;
const Wrapper = styled.div`
  background: white;
  border-radius: 7px;
  padding: 20px;
  gap: 1rem;
  //   width: 30%;
  // height: 65%;
  ${tablet({ width: "80%" })};
`;

const Header = styled.div``;
const HeaderMainText = styled.h2`
  font-weight: 700;
  text-align: center;
`;
const HeaderSmText = styled.p`
font-size:.79rem;
 text-align: center;
`;
const HeaderBdText = styled.h4``;

const ResetEmail = styled.div`
width:100%;
text-align:center;
`
const ResetEmailBgText = styled.h4``
const ResetEmailBdText = styled.span`
  margin:0 2px;
   font-size:.91rem;
`

const ChangeEmailCon = styled.div`
width:60%;
`
const ChangeEmailInput = styled.input`
  width: 100%;
  height: 100%;
  outline: none;
  border: none;
  border-bottom: 1px solid grey;
  // border-radius: 5px;
  padding: 5px 3px;
`;
const ChangeEmailButton= styled.div``

const OtpPinsInput = styled.input`
  outline: none;
  height: 100%;
  width: 100%;
  border: none;
  border-bottom: 2px solid grey;
  margin: 0 10px;
  font-size: 30px;
`;

const ButtonCon = styled.div``;
const Button = styled.button`
  background: #2563eb;
  padding: 0.6rem 2.2rem;
  border-radius: 4px;
  color: white;
  border: none;
  outline: none;
  font-size: 15px;
  font-weight: 600;
`;

const VerifyOTP = () => {
  const [otp, setOtp] = useState("");
  const [changeEmail,setChangeEMail]=useState(true)
  const handleSubmit = async ()=>{
    try{
      const res = await axios.get("https://industrialiot.onrender.com/api/verifyemail",{
        email:"chinonsoxavier26@gmail.com"
      });
      console.log(res)
    }catch(err){
      console.log(err)
    }
  }

  return (
    <Container className="flex aic jcc">
      <Wrapper className="flex aic jcc fdc">
        <Header className="w100 flex aic jcc fdc">
          {
            !changeEmail ?
          <>
          <HeaderMainText>
            Verify your Email
            {/* Please enter the One-Time-Password to verify your account */}
          </HeaderMainText>
          <HeaderSmText>
            Type in the 6 digit OTP code that was sent to your Email.
            {/* A one time password has been sent to email */}
          </HeaderSmText>
        </>
        :
        <>
        <HeaderMainText>
          Enter your Email Address to continue
        </HeaderMainText>
        <HeaderSmText>
          We care about your privacy,like our own
        </HeaderSmText>
        </>
}
        </Header>
        {!changeEmail ? 
        <>
          <ResetEmail className=" aifs jcfs fdc w100 " >

          <ResetEmailBgText>
            Chinonsoxavier26gmail.com
          </ResetEmailBgText>
          <ResetEmailBdText>
            Wrong Email?
          </ResetEmailBdText>
          <ResetEmailBdText style={{color:"#2563eb",fontWeight:600}} >Change</ResetEmailBdText>
          </ResetEmail>
        
        <OTPInput
          value={otp}
          onChange={setOtp}
          numInputs={6}
          renderSeparator={<span> </span>}
          renderInput={(props) => <OtpPinsInput {...props} />}
        />
        </>
        :
        <ChangeEmailCon className="flex aic jcc" >
            <ChangeEmailInput placeholder="update your email" type="email" defaultValue='email' />
            {/* <ChangeEmailButton>Change Email</ChangeEmailButton> */}
      </ChangeEmailCon>
}
        <ButtonCon className="flex aic jcc">
          {
            changeEmail ? 
            <Button className="cp"  >
               Continue
            </Button>
            :

          <Button className="cp" type="submit" onClick={handleSubmit}>
            VERIFY
          </Button>
          }
        </ButtonCon>
        {/* <div>
          <div>
            <p>Didn`t recieve otp? send again</p>
            <p>change email</p>
          </div>
        </div> */}
      </Wrapper>
    </Container>
  );
};

export default VerifyOTP;
