import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { tablet } from "../responsive";
import styled from "styled-components";
const Container = styled.div`
  width: 100vw;
  height: 100dvh;
  background: #dbeafe;
  color: #3c3c3c;
`;
const Wrapper = styled.div`
  width: 30%;
  // height: 65%;
  ${tablet({ width: "80%" })};
`;

const Header = styled.div``
const HEaderMainText = styled.h3`
font-weight:700;
`;
const HEaderSmText = styled.p``;

const VerifyOTP = () => {
 
  return (
    <Container className="flex aic jcc">
     <Wrapper>
     <Header className="w100 flex aic jcc fdc" >
        <HEaderMainText>Verify</HEaderMainText>
        <HEaderSmText>your verification code has been sent to your email</HEaderSmText>
     </Header>
     </Wrapper>
    </Container>
  );
};

export default VerifyOTP;
