import { useContext } from "react";
import { Link, useNavigate } from "react-router-dom";
import styled from "styled-components"
import { UserContext } from "../context/context";
import { useState } from "react";

const Container = styled.div` 
width:100dvw;
height:100dvh;
background:#fefaf1;
background:#ffffff;
`
const Wrapper = styled.div`
max-width:30%;
// background:red;
gap:1rem;
`
const Header = styled.div``
const HeaderBgText = styled.p`
  font-size: 2.2rem;
  font-weight: 500;
  text-align: center;
`;
const HeaderSmText = styled.p`
text-align:center;
font-weight:100;
font-size:.85rem;
opacity:.8;
`;

const InputCon = styled.div`
width:100%;
`
const InputWrapper = styled.div`
  width: 100%;
  border: 1px solid grey;
  height: 100%;
  background: transparent;
  margin: 5px 0;
`;
const InputLabel = styled.label`
`;
const Input = styled.input`
  width: 100%;
  height: 100%;
  border: none;
  outline: none;
  color: gray;
  background: transparent;
  padding:2px 7px;
`;

const ButtonCon = styled.div`
   width:100%;
`;
const Button = styled.button`
  cursor: pointer;
  background: ${(props) =>
    props.type !== "outline" ? "#297f85" : "transparent"};
  padding: ${(props) => (props.type !== "outline" ? "10px 35px" : "10px 8px")};
  color: ${(props) => (props.type !== "outline" ? "white" : "#297f85")};
  border: none;
  width: 100%;
  box-sizing: border-box;
  font-weight:500;
  white-space: nowrap;
`;

function ResetPassword() {

  const user = useContext(UserContext);
  const dispatchUserEvent = useContext(UserContext);
  const [newEmail, setNewEmail] = useState(user.user.email);
  // console.log(user);

  return (
    <Container className="flex aic jcc">
      <Wrapper className="flex aic jcc fdc">
        <Header>
          <HeaderBgText>Reset Your Password</HeaderBgText>
          <HeaderSmText>
            Input your account email and we`ll send an OTP to your Email,To
            reset your password!.
          </HeaderSmText>
        </Header>
        {user.user ? (
          <>
            <InputCon>
              <InputLabel htmlFor="resetPassword">Email</InputLabel>
              <InputWrapper>
                <Input
                  placeholder="Enter your account email"
                  type="email"
                  id="resetPassword"
                  className=""
                />
              </InputWrapper>
            </InputCon>
            <ButtonCon className="flex aic jcc">
              <Button>Reset Password</Button>
              <Link className="link" to="/login">
                <Button type="outline" style={{ textDecoration: "underline" }}>
                  Return to login
                </Button>
              </Link>
            </ButtonCon>
          </>
        ) : (
          <ResetPassWrapper>
            <ResetPassTextCon>
              <ResetPassText>Reset your password</ResetPassText>
            </ResetPassTextCon>
            <ResetPassCon>
              {/* <ResetPassInputCon>
                <ResetPassLabelCon id="resetPassOld">
                  Enter your old password
                </ResetPassLabelCon>
                <ResetPassInput id="resetPassOld" className="" />
              </ResetPassInputCon>
              <ResetPassInputCon>
                <ResetPassLabelCon id="resetPassOld">
                  Enter your old password
                </ResetPassLabelCon>
                <ResetPassInput id="resetPassOld" className="" />
              </ResetPassInputCon> */}
              <InputCon>
                <Inputlabel Htmlfor="password">
                  {" "}
                  Enter your old password
                </Inputlabel>
                <InputContainer className="flex aic jcc">
                  {/* <Lock /> */}
                  <Input
                    ref={(el) => PasswordInputRef.current.push(el)}
                    className=""
                    id="password"
                    type="password"
                    placeholder="Enter your old password
"
                    // onChange={(e) => handleSubmit(e, "password")}
                  />
                  {passVisible === 0 ? (
                    <RemoveRedEye
                      sx={{
                        cursor: "pointer",
                        position: "absolute",
                        right: "10px",
                      }}
                      onClick={() => ToggleVisibility(0)}
                      id="visiblePassword_Icon"
                    />
                  ) : (
                    <VisibilityOff
                      sx={{
                        cursor: "pointer",
                        position: "absolute",
                        right: "10px",
                      }}
                      onClick={() => ToggleVisibility(0)}
                      id="visiblePassword_Icon"
                    />
                  )}
                </InputContainer>
              </InputCon>
              <InputCon>
                <Inputlabel Htmlfor="password">
                  {" "}
                  Enter your new password
                </Inputlabel>
                <InputContainer className="flex aic jcc">
                  {/* <Lock /> */}
                  <Input
                    ref={(el) => PasswordInputRef.current.push(el)}
                    className=""
                    id="password"
                    type="password"
                    placeholder="Enter your new password"
                    // onChange={(e) => handleSubmit(e, "password")}
                  />
                  {passVisible === 1 ? (
                    <RemoveRedEye
                      sx={{
                        cursor: "pointer",
                        position: "absolute",
                        right: "10px",
                      }}
                      onClick={() => ToggleVisibility(1)}
                      id="visiblePassword_Icon"
                    />
                  ) : (
                    <VisibilityOff
                      sx={{
                        cursor: "pointer",
                        position: "absolute",
                        right: "10px",
                      }}
                      onClick={() => ToggleVisibility(1)}
                      id="visiblePassword_Icon"
                    />
                  )}
                </InputContainer>
              </InputCon>
            </ResetPassCon>
            <Back className=" aic jcc">
              <BackBtn className="flex aic jcfs">
                <div className="flex aic jcfs  w100 ">
                  <KeyboardDoubleArrowLeft sx={{ fontSize: "1.2rem" }} />
                </div>
                <div className="flex aic jcfs w100 "> Back</div>
              </BackBtn>
            </Back>
          </ResetPassWrapper>
        )}
      </Wrapper>
    </Container>
  );
}

export default ResetPassword
